monitoring_results.query:
PREFIX datacite: <http://purl.org/spar/datacite/>
PREFIX literal: <http://www.essepuntato.it/2010/06/literalreification/>
PREFIX fabio: <http://purl.org/spar/fabio/>

ASK {
    ?br1 datacite:hasIdentifier/literal:hasLiteralValue ?lit ;
    a fabio:Expression .
    ?br2 datacite:hasIdentifier/literal:hasLiteralValue ?lit ;
    a fabio:Expression .
    FILTER(?br1 != ?br2)
}

